<!-- navbar -->
nav.navbar.navbar-inverse.navbar-fixed-top>div.container-fluid>div.container>(div.navbar-header>a.navbar-brand>img[src="http://placehold.it/180x23"])+(button.navbar-toggle.collapsed[type="button"][data-toggle="collapse"][data-target="#nav-res"][aria-expanded="true"]>span.icon-bar*3)+ul.nav.navbar-nav.list-inline>(li>a{Page $})*5+li.divider+(li.idioma>a.flagIdioma#idioma$>img[src="http://placehold.it/30x30"])*2+(li.redesBtn>a.redesIcon#facebook>i.fa.fa-facebook[aria-hidden="true"])+(li.redesBtn>a.redesIcon#instagram>i.fa.fa-instagram[aria-hidden="true"])+(li.redesBtn>a.redesIcon#youtube>i.fa.fa-youtube[aria-hidden="true"])
<!-- HEADER > Slideshow -->
header.block>section#mainslideshow.container-fluid.owl-corousel>(div.item>(img.visible-lg-block[src="http://placehold.it/1920x1200?text=TITULO-$"])+(img.hidden-lg[src="http://placehold.it/1200x800?text=TITULO-$"]))*5